import {react} from "react"
import { Link } from "react-router-dom";
import BookCard from "./BookCard";
import { getAll } from "./BooksAPI";
import {useEffect} from "react"
import BookShelf from "./BookShelf";


const BookShelves = (props)=>{


  const FilteredBooksList = (ListName)=> props.Books.filter((book)=>book.shelf===`${ListName}`)

    return (
        <div className="list-books">
        <div className="list-books-title">
          <h1>MyReads</h1>
        </div>
        <BookShelf ShelfTitle="Currently Reading" BooksList={FilteredBooksList(Currently)}/>
        <BookShelf ShelfTitle="Want to Read" BooksList={props.AllBooks}/>
        <BookShelf ShelfTitle="read" BooksList={props.AllBooks}/>
        <Link className="open-search" to="/search" onClick={props.openSearch} >Add a book</Link>
      </div>

    )
};

export default BookShelves;
