import {react} from "react"
import { useState,useEffect } from "react";
import { get, getAll, search } from "./BooksAPI";
import * as BooksAPI from "./BooksAPI"
import BookCard from "./BookCard";

const SearchComponent = ({Search,returnButton,SearchedBooks,AddBookto})=>{

    const [SearchInput,SetSearchInput] = useState("")

    const InputHandle = (event)=>{
        SetSearchInput(event.target.value)
        console.log(SearchInput)
        Search(`${SearchInput}`)
                             
    }

    const[SearchResults,SetSearchResults]=useState([])

    console.log(SearchedBooks)

    const Results = searchedBooks.map(book => (
      <BookCard 
      title={book.title}
      authors={book.authors}
      CoverImage={book.imageLinks.smallThumbnail}
      bookShelf={book.shelf}
      bookId={book.id}
      AddBookto={AddBookto}
      />
  ))

   /*useEffect(()=>{
        const SearchBooks = async (SearchInput) => {
          const res = await BooksAPI.search(SearchInput,5);
          SetSearchResults(res)
        };
        SearchBooks(SearchInput);
        
     
      },[SearchInput])*/



            
    return (

        <div className="search-books">
            <div className="search-books-bar">
              <a
                className="close-search"
          onClick={returnButton}
              >
                Close
              </a>
              <div className="search-books-input-wrapper">
                <input
                  type="text"
                  placeholder="Search by title, author, or ISBN"
                  onChange={InputHandle}
                  value ={SearchInput}
                  
                />
              </div>
            </div>
            <div className="search-books-results">
              <ol className="books-grid"></ol>
              {Results}          
            </div>
          </div>

    )
};

export default SearchComponent;
