import {react} from "react"
import { useState,useEffect } from "react";
import { get, getAll, search } from "./BooksAPI";
import * as BooksAPI from "./BooksAPI"
import BookCard from "./BookCard";


const SearchComponent = ({Search,returnButton,AddBookto})=>{

    const [SearchInput,SetSearchInput] = useState("")
    const [SearchedBooks,setSearchedBooks]= useState([])





    const InputHandle = (event)=>{
      SetSearchInput(event.target.value)
      console.log(SearchInput)
      
                           
  }

    useEffect(()=>{
      Search = async (SearchInput) => {
       const res = await BooksAPI.search(`${SearchInput}`)
       setSearchedBooks(res)
       console.log(res)
     };
     Search(SearchInput)
     if(typeof(SearchedBooks)!== "undefined"){
     console.log(SearchedBooks)
     console.log(typeof(SearchedBooks))}


   },[SearchInput])



   let Results
   if(typeof(SearchedBooks)!== "undefined"){
    Results =  SearchedBooks.map(book=>(
      <BookCard 
      title={book.title}
      authors={book.authors}
      CoverImage={book.imageLinks.smallThumbnail}
      bookShelf={book.shelf}
      bookId={book.id}
      book={{book}}
      AddBookto={AddBookto}
    
      />
    ))
}

   
           
    return (

        <div className="search-books">
            <div className="search-books-bar">
              <a
                className="close-search"
          onClick={returnButton}
              >
                Close
              </a>
              <div className="search-books-input-wrapper">
                <input
                  type="text"
                  placeholder="Search by title, author, or ISBN"
                  onChange={InputHandle}
                  value ={SearchInput}
                  
                />
              </div>
            </div>
            <div className="search-books-results">
              <ol className="books-grid"></ol>
            </div>
          </div>

    )
};




export default SearchComponent;
