import {react} from "react"
import BookCard from "./BookCard";


const BookShelf = ({BooksList,ShelfTitle,AllBooks})=>{


    console.log(BooksList)

    const addBooktoList = (book,ShelfState)=> {
      const AddBook = async ()=> {
        const res = await BooksAPI.update(book,ShelfState)
        setAllBooks(AllBooks.concat(res))
      }}   




    return (

    <div className="bookshelf">
        <h2 className="bookshelf-title">{ShelfTitle}</h2>
        <div className="bookshelf-books">
          <ol className="books-grid">
            {BooksList.map(book=>(
                <BookCard 
                title={book.title}
                authors={book.authors}
                CoverImage={book.imageLinks.smallThumbnail}
                bookShelf={book.shelf}
                bookId={book.id}
                addBooktoList = {()=>addBooktoList(book.id)}

                />
            ))}                                

          </ol>
        </div>
      </div>

    )
};

export default BookShelf;
