import "./App.css";
import { useState } from "react";
import {Routes,Route,useNavigate} from "react-router-dom"
import {Link} from "react-router-dom"
import SearchComponent from "./SearchComponent";
import BookShelves from "./BookShelves"
import {useEffect} from "react"
import * as BooksAPI from "./BooksAPI";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  let navigate = useNavigate()

const [AllBooks,setAllBooks]= useState([]);

useEffect(()=>{
  const getBooks = async () => {
    const res = await BooksAPI.getAll();
    setAllBooks(res)
  };
  getBooks();


  const addBooktoList =(book,ShelfState)=> {
    const AddBook = async ()=> {
      const res = await BooksAPI.update(book,ShelfState)
      setAllBooks(AllBooks.concat(res))
    }

  }
 
},[])



  return (
    <div className="app">
      <Routes>
      {showSearchPage ? (
        
          <Route path = "/search" element={
            <SearchComponent returnButton={() => setShowSearchpage(navigate("/"))}/>
          }
          
          />
          
        
        
      ) : (
        <Route exact path="/" element={
          <BookShelves Books={AllBooks} openSearch={() => setShowSearchpage(!showSearchPage)} onBookSelection={()=> addBooktoList(book,ShelfState)}/>

        }/>

      )}
    </Routes>
    </div>
  );
}

export default App;
