import {react} from "react"
import { useState,useEffect } from "react";
import * as BooksAPI from "./BooksAPI";


const BookCard = ({CoverImage,title,authors,bookShelf,bookId})=>{
 
  let ShelfState
  if (bookShelf=== "currentlyReading"){ ShelfState = "currentlyReading"

  } else if( bookShelf=== "wantToRead"){ ShelfState = "wantToRead"}
  else if ( bookShelf=== "wantToRead"){ ShelfState = "read"}
  else {ShelfState = "none"}

  const[BookSelection,SetBookSelection]=useState(ShelfState)

  const handleSelect = (event)=>{
    SetBookSelection(event.value)
    addBookto(bookId,BookSelection)
  }

  useEffect(()=>{
    const addBookto = async () => {
      const res = await BooksAPI.update(bookId,BookSelection);
      SetBookSelection(res)
    };
    ;
   
  },[])

    return(
      
      

        <div className="book">
                      <div className="book-top">
 
                        <img className="book-cover" src={CoverImage} width="128" height="193"></img>
                        <div className="book-shelf-changer">
                          <select>
                            <option onSelect={handleSelect} value="none" disabled>Move to...</option>
                            <option onSelect={handleSelect} value="currentlyReading">Currently Reading</option>
                            <option onSelect={handleSelect} value="wantToRead">Want to Read</option>
                            <option onSelect={handleSelect} value="read">Read</option>
                            <option onSelect={handleSelect} value="none">None</option>
                          </select>
                        </div>
                      </div>
                      <div className="book-title">{title}</div>
                      <div className="book-authors">{authors}</div>
                    </div>
    )


};

export default BookCard;
